// Generated by Selenium IDE
const { Builder, By, Key, until } = require('selenium-webdriver')
const assert = require('assert')

describe('Verificación de inputs', function() {
  this.timeout(30000)
  let driver
  let vars
  beforeEach(async function() {
    driver = await new Builder().forBrowser('chrome').build()
    vars = {}
  })
  afterEach(async function() {
    await driver.quit();
  })
  it('Verificación de inputs', async function() {
    // Test name: Verificación de inputs
    // Step # | name | target | value
    // 1 | open | / | 
    await driver.get("http://localhost:3000/")
    // 2 | setWindowSize | 1280x800 | 
    await driver.manage().window().setRect({ width: 1280, height: 800 })
    // 3 | click | css=.input:nth-child(2) | 
    await driver.findElement(By.css(".input:nth-child(2)")).click()
    // 4 | click | css=.input:nth-child(3) | 
    await driver.findElement(By.css(".input:nth-child(3)")).click()
    // 5 | click | css=.input:nth-child(4) | 
    await driver.findElement(By.css(".input:nth-child(4)")).click()
    // 6 | close |  | 
    await driver.close()
  })
})
